import React from 'react';

interface BrandLegendListboxProps {
  brands: string[];
}

const brandColors = [
  'text-blue-600',
  'text-orange-500',
  'text-green-500',
  'text-yellow-500',
  'text-purple-500',
  'text-pink-500',
  'text-teal-500',
  'text-red-500',
];

const BrandLegendListbox: React.FC<BrandLegendListboxProps> = ({ brands }) => {
  const legendItems = brands.map((brand, index) => ({
    name: brand,
    color: brandColors[index % brandColors.length],
  }));

  return (
    <div className="flex flex-col bg-white p-4 rounded-lg border border-border-gray w-64 h-[400px]">
      <div className="flex-grow overflow-y-auto space-y-2">
        {/* My Brand */}
        {legendItems.slice(0, 1).map((item, index) => (
          <div key={index} className="flex items-center gap-2 text-sm">
            <input type="checkbox" defaultChecked className={`form-checkbox ${item.color}`} />
            <span className="text-text-dark">{item.name}</span>
          </div>
        ))}

        {/* Competitors separator */}
        <div className="text-xs font-bold text-text-dark mt-2 mb-1">
          Competitors:
        </div>

        {/* Other brands */}
        {legendItems.slice(1).map((item, index) => (
          <div key={index + 1} className="flex items-center gap-2 text-sm">
            <input type="checkbox" defaultChecked className={`form-checkbox ${item.color}`} />
            <span className="text-text-dark">{item.name}</span>
          </div>
        ))}
      </div>
      <div className="mt-4 pt-2 border-t border-border-gray flex justify-between items-center text-xs">
        <button className="text-primary-blue font-bold">Select Top Competitors</button>
        <span className="text-text-secondary">4/6</span>
      </div>
    </div>
  );
};

export default BrandLegendListbox;
