import React from 'react';

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ children, className, ...props }) => {
  const baseClasses = `
    px-4 py-2
    text-xs font-medium font-dm-sans leading-4
    rounded-[18px]
    transition-all duration-150
  `;

  const stateClasses = `
    text-primary-blue bg-white
    shadow-button-outline
    hover:shadow-button-outline-hover hover:bg-primary-blue-light-hover
    active:shadow-button-outline-hover active:bg-primary-blue-light-active
    disabled:text-text-disabled disabled:shadow-button-outline disabled:bg-transparent disabled:cursor-not-allowed
  `;

  return (
    <button
      className={`
        ${baseClasses}
        ${stateClasses}
        ${className || ''}
      `.trim().replace(/\s+/g, ' ')}
      {...props}
    >
      {children}
    </button>
  );
};

export default Button;
