import React from 'react';

interface ViewSwitcherProps {
  isTableView: boolean;
  setIsTableView: (isTable: boolean) => void;
}

const ViewSwitcher: React.FC<ViewSwitcherProps> = ({ isTableView, setIsTableView }) => {
  const buttonClasses = (isActive: boolean) => `
    px-3 py-2.5 text-sm font-dm-sans rounded-lg
    transition-colors duration-150
    ${isActive ? 'bg-primary-blue-light-active text-primary-blue' : 'bg-white text-text-dark'}
  `;

  return (
    <div className="flex rounded-lg border border-border-gray p-0.5 overflow-hidden bg-white">
      <button
        onClick={() => setIsTableView(true)}
        className={buttonClasses(isTableView)}
      >
        Table View
      </button>
      <button
        onClick={() => setIsTableView(false)}
        className={buttonClasses(!isTableView)}
      >
        Graph View
      </button>
    </div>
  );
};

export default ViewSwitcher;
