import React from 'react';

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ children, className, ...props }) => {
  const defaultClasses = `
    px-4 py-2 text-xs border border-border-gray rounded-xl text-primary-blue font-medium leading-4 
    transition-colors duration-150 
    hover:bg-primary-blue-light-hover hover:border-primary-blue 
    active:bg-primary-blue-light-active active:border-primary-blue 
    disabled:text-[#b6bec6] disabled:border-border-gray disabled:bg-transparent disabled:cursor-not-allowed
  `;

  return (
    <button className={` ${defaultClasses} ${className || ''} `.trim().replace(/\s+/g, ' ')} {...props}>
      {children}
    </button>
  );
};

export default Button;
