import React from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from 'recharts';

const data = [
  { name: 'Mar 20', myBrand: 22, competitor1: 15, competitor2: 12, competitor3: 8 },
  { name: 'Apr 20', myBrand: 25, competitor1: 18, competitor2: 15, competitor3: 10 },
  { name: 'May 20', myBrand: 28, competitor1: 20, competitor2: 18, competitor3: 12 },
  { name: 'Jun 20', myBrand: 26, competitor1: 22, competitor2: 20, competitor3: 15 },
  { name: 'Jul 20', myBrand: 30, competitor1: 25, competitor2: 22, competitor3: 18 },
  { name: 'Aug 20', myBrand: 32, competitor1: 28, competitor2: 25, competitor3: 20 },
  { name: 'Sep 20', myBrand: 35, competitor1: 30, competitor2: 28, competitor3: 22 },
  { name: 'Oct 20', myBrand: 38, competitor1: 32, competitor2: 30, competitor3: 25 },
  { name: 'Nov 20', myBrand: 36, competitor1: 35, competitor2: 32, competitor3: 28 },
  { name: 'Dec 20', myBrand: 40, competitor1: 38, competitor2: 35, competitor3: 30 },
  { name: 'Jan 21', myBrand: 42, competitor1: 40, competitor2: 38, competitor3: 32 },
  { name: 'Feb 21', myBrand: 45, competitor1: 42, competitor2: 40, competitor3: 35 },
];

const InteractiveLineChart: React.FC = () => {
  return (
    <div className="flex-1 bg-white p-4 rounded-lg border border-border-gray h-[400px]">
      <ResponsiveContainer width="100%" height="100%">
        <LineChart
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" vertical={false} />
          <XAxis 
            dataKey="name" 
            tick={{ fill: '#b6bec6', fontSize: 11 }} 
            axisLine={false} 
            tickLine={false} 
          />
          <YAxis 
            tick={{ fill: '#b6bec6', fontSize: 11 }} 
            axisLine={false} 
            tickLine={false}
            tickFormatter={(value) => `${value}%`}
          />
          <Tooltip
            contentStyle={{
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              border: '1px solid #ccc',
              borderRadius: '4px',
              fontSize: '12px',
            }}
          />
          <Line type="monotone" dataKey="myBrand" stroke="#3e74fe" strokeWidth={2} dot={false} />
          <Line type="monotone" dataKey="competitor1" stroke="#ff7a1a" strokeWidth={2} dot={false} />
          <Line type="monotone" dataKey="competitor2" stroke="#00ca9a" strokeWidth={2} dot={false} />
          <Line type="monotone" dataKey="competitor3" stroke="#ffb800" strokeWidth={2} dot={false} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default InteractiveLineChart;
