import React, { useState } from 'react';

const ViewSwitcher: React.FC = () => {
  const [isTableView, setIsTableView] = useState(true);

  return (
    <div className="relative flex rounded-lg bg-white border border-border-gray">
      <div
        className={`
          absolute top-0 left-0 h-full
          rounded-lg bg-primary-blue-light-active
          transition-transform duration-300 ease-in-out
        `}
        style={{
          width: '50%',
          transform: isTableView ? 'translateX(0)' : 'translateX(100%)',
        }}
      />
      <button
        onClick={() => setIsTableView(true)}
        className={`
          flex-1 px-3 py-2.5 text-sm z-10
          transition-colors duration-150
          ${isTableView ? 'text-primary-blue' : 'text-text-dark'}
        `}
      >
        Table Name
      </button>
      <div className="w-px bg-border-gray h-full" />
      <button
        onClick={() => setIsTableView(false)}
        className={`
          flex-1 px-3 py-2.5 text-sm z-10
          transition-colors duration-150
          ${!isTableView ? 'text-primary-blue' : 'text-text-dark'}
        `}
      >
        Graph Name
      </button>
    </div>
  );
};

export default ViewSwitcher;
