import React from 'react';

export const PromotionSection: React.FC = () => {
  const promotionLinks = [
    'Amazon Funnel',
    'Trend Analysis',
    'Brand Sales Performance',
    'Brand Search Optimization'
  ];

  return (
    <div className="bg-white border border-blue-600 rounded-lg p-6">
      <div className="flex items-center gap-6 mb-6">
        {/* Icon */}
        <div className="w-12 h-12 bg-blue-100 rounded flex items-center justify-center">
          <svg className="w-8 h-8 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
          </svg>
        </div>

        {/* Text Content */}
        <div className="flex-1">
          <h3 className="text-sm font-bold text-gray-900 mb-2">
            Dig deeper into what's moving your share
          </h3>
          <p className="text-sm text-gray-600">
            Jump into focused views to see how shoppers find, choose, and buy your productsâ€”and pinpoint the levers to pull next.
          </p>
        </div>
      </div>

      {/* Action Links */}
      <div className="grid grid-cols-4 gap-4">
        {promotionLinks.map((link, index) => (
          <div key={index} className="bg-white border border-blue-600 rounded-lg p-3 flex items-center gap-2">
            <div className="w-6 h-6 bg-white border border-gray-200 rounded flex items-center justify-center">
              <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
              </svg>
            </div>
            <span className="text-xs text-gray-900">{link}</span>
          </div>
        ))}
      </div>
    </div>
  );
};
