import React, { useState } from 'react';

const ViewSwitcher: React.FC = () => {
  const [isTableView, setIsTableView] = useState(true);

  const buttonClasses = (isActive: boolean) => `
    px-3 py-2.5 text-sm font-dm-sans
    transition-colors duration-150
    ${isActive ? 'bg-primary-blue-light-active text-primary-blue' : 'bg-white text-text-dark'}
  `;

  return (
    <div className="flex rounded-lg border border-border-gray overflow-hidden">
      <button
        onClick={() => setIsTableView(true)}
        className={buttonClasses(isTableView)}
      >
        Table View
      </button>
      <div className="w-px bg-border-gray" />
      <button
        onClick={() => setIsTableView(false)}
        className={buttonClasses(!isTableView)}
      >
        Graph View
      </button>
    </div>
  );
};

export default ViewSwitcher;
